import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import paginate from 'paginate-array';
import ReactTable from "react-table"; 
import 'react-table/react-table.css'
const getColor = (type) => {
  if (type === "Classique" ) return 'red';
  if (type === "IP" ) return 'blue';
  return '';
};
const Todo = props => (
    <tr style={{ color: getColor(props.todo.type) }}>
        <td>{props.todo.central}</td>
        <td>{props.todo.constructeur}</td>
        <td>{props.todo.type}</td>
        <td>{props.todo.equipements}</td>
        <td>{props.todo.nombreab}</td>
        <td>{(((props.todo.nombreab/props.todo.equipements)*100).toFixed(2) )}</td>
        <td>
            <Link to={"/edit/"+props.todo._id}>Edit</Link> | <a href="#" onClick={() => { props.deleteTodo(props.todo._id) }}>Delete</a>
        </td>
    </tr>
)
export default class TodosList extends Component {
    constructor(props) {
        super(props);
        this.deleteTodo = this.deleteTodo.bind(this)
        this.state ={
            todos: [],
            loading:true
          }
                     
    }
componentDidMount() {
    axios.get('http://localhost:4000/todos/')
        .then(response => {

            this.setState(
                {
                    todos: response.data,
            });
        })
        .catch(function (error) {
            console.log(error);
        })
}
deleteTodo(id) {
    axios.delete('http://localhost:4000/todos/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
      todos: this.state.todos.filter(el => el._id !== id)
    })
  }
todoList() {
    return this.state.todos.map(currentTodo => {
        return <Todo todo={currentTodo}  deleteTodo={this.deleteTodo} key={currentTodo._id} />;
    })
}

render() {
  const columns = [{  
    Header: 'Central',  
    accessor: 'central',
   }
   ,{  
    Header: 'Constructeur',  
    accessor: 'constructeur' ,
    }
   
   ,{  
   Header: 'Type',  
   accessor: 'type' ,
   }
   ,{  
   Header: 'Equipements',  
   accessor: 'equipements',
   },
   {  
    Header: 'Nombre abonnées',  
    accessor: 'Nombre abonnées',
    },
    {  
      Header: 'Taux',  
      accessor: 'taux',
      }
]
    return (
        <div>
            <h3></h3>
            <table className="table table-striped" style={{ marginTop: 20 }}>
                <thead>
                    <tr>
                        <th>Central</th>
                        <th>Constructeur</th>
                        <th>Type</th>
                        <th>Equipements</th>
                        <th>Nombre d'abonnées</th>
                        <th>Taux</th>
                    </tr>
                </thead>
                <tbody>
                    { this.todoList() }
                </tbody>
            </table>
        </div>
    )
}
}
