import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import ReactPaginate from 'react-paginate';

const Todo = props => (
    <tr>
        <td>{props.todo.central}</td>
        <td>{props.todo.constructeur}</td>
        <td>{props.todo.type}</td>
        <td>{props.todo.equipements}</td>
        <td>{props.todo.nombreab}</td>
        <td>{props.todo.taux}</td>
        <td>
            <Link to={"/edit/"+props.todo._id}>Edit</Link>
        </td>
    </tr>
)
export default class TodosList extends Component {
    constructor(props) {
        super(props);
        this.state ={
            todos: [],
            perPage: 3,
            page: 0,
            pages: 0,};
    }


componentDidMount() {
    axios.get('http://localhost:4000/todos/')
        .then(response => {
            const {perPage} = this.state;
            this.setState(
                {todos: response.data,
                pages: Math.floor(this.state.todos.length /perPage)
            });
        })
        .catch(function (error) {
            console.log(error);
        })
}
handlePageClick = (event) => {
    let page = event.selected;
    this.setState({page})
  }
/*todoList() {
    return this.state.todos.map(function(currentTodo, i) {
        return <Todo todo={currentTodo} key={i} />;
    });
}*/

render() {
    const {page, perPage, pages, todos} = this.state;
   let items = todos.slice(page * perPage, (page + 1) * perPage);
    let notes =items.map(function(currentTodo, i) {
        return <Todo todo={currentTodo} key={i} />;
    })|| '';
    return (
        <div>
            <h3>Todos List</h3>
            <table className="table table-striped" style={{ marginTop: 20 }}>
                <thead>
                    <tr>
                        <th>Central</th>
                        <th>Constructeur</th>
                        <th>Type</th>
                        <th>Equipements</th>
                        <th>Nombre d'abonn√©es</th>
                        <th>Taux</th>
                    </tr>
                </thead>
                <tbody>
                    { notes }
                </tbody>
            </table>
            <ReactPaginate
         previousLabel={'prev'}
         nextLabel={'next'}
         pageCount={pages}
         onPageChange={this.handlePageClick}
         containerClassName={'pagination'}
         activeClassName={'active'}
       />
        </div>
    )
}
}
